*{box-sizing:border-box; -webkit-box-sizing:border-box; -moz-box-sizing:border-box; -o-box-sizing:border-box; -ms-box-sizing:border-box;}

html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video{border:0;font-size:100%;font:inherit;vertical-align:top;margin:0;padding:0}article,aside,details,figcaption,figure,footer,header,hgroup,menu,nav,section{display:block}body{line-height:1}ol,ul{list-style:none}blockquote,q{quotes:none}blockquote:before,blockquote:after,q:before,q:after{content:none}table{border-collapse:collapse;border-spacing:0}	/* Resets */

a,img{border:0; outline:none;}textarea:focus,input:focus{outline:0;}*:focus{outline:0;}

body, html	{-webkit-font-smoothing:antialiased; -moz-osx-font-smoothing:grayscale;}
body, html	{width:100%; margin:auto; padding:0; background-color:#efefef; font:16px 'Signika','Arial','Liberation Sans','Helvetica Neue',sans-serif; line-height:1.4; color:#202125; font-size-adjust:0.4; font-weight:normal; font-style:normal;}
html		{height:100%;}
body		{min-width:100%; min-height:100%; display:table;}
a			{-webkit-transition:all .2s ease-in-out; -moz-transition:all .2s ease-in-out; -o-transition:all .2s ease-in-out; -ms-transition:all .2s ease-in-out; transition:all .2s ease-in-out; cursor:pointer;}
a, img		{border:0; outline:none;}
a			{overflow-wrap:break-word; word-wrap:break-word; word-break:break-word; -ms-hyphens:auto; -moz-hyphens:auto; -webkit-hyphens:auto; hyphens:auto;} /* Fix long urls */
a:focus		{color:#3a55a9; text-decoration:none;}
a:link  	{color:#3a55a9; text-decoration:none;}
a:visited	{color:#3a55a9; text-decoration:none;}
a:hover		{color:#202125; text-decoration:none;}
a:active	{color:#3a55a9; text-decoration:none;}
b			{font-weight:bold;}
i			{font-style:italic;}

h1			{font-size:4em; font-weight:normal;}
h2			{font-size:2em; font-weight:normal;}


/*	Style for wrappers
	============================================= */

.wrap		{width:960px; margin:0 auto;}
.wrap.max	{width:100%;}
.wrap.page	{position:absolute; width:1124px; bottom:0; left:-80px;}
.wrap.mob	{display:none;}

.col		{display:inline-block; vertical-align:top; margin-bottom:20px;}
.col.col-2	{width:42.5%;}
.col.col-2:nth-of-type(even){margin-left:4.5%;}
.col.col-3	{width:30%;}
.col.col-3:nth-of-type(3n+3){margin-left:4.5%; margin-right:4.5%;}

#content	{display:table-row-group;}

.blog_list_wrap		{margin-top:60px; margin-bottom:60px;}
.blog_list_wrap.big	{margin:0;}


/*	Navigation
	============================================= */

.nav		{width:100%; padding:20px 40px; background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQYV2PMTk7+P3XuXEYGKAAzkAXhMjBBuABMCwABTAwFcFXmywAAAABJRU5ErkJggg==');}
.nav div	{display:inline-block; vertical-align:middle; width:50%;}
.nav div:nth-of-type(2)	{text-align:right;}
.nav a		{color:#2a3133; font-size:20px; font-weight:300; border-bottom:3px solid #efefef;}
.nav a:hover{border-bottom:3px solid #2a3133;}
.nav a.logo	{display:block; background-color:#42494B; padding:10px 15px 15px 15px; width:80px; border-radius:4px; border:0; margin:auto;}
.nav a.logo span		{display:block; border-bottom:3px solid #fff; color:#fff; font-weight:400;}
.nav a.logo:hover span	{border-bottom:3px solid #00d8ff; color:#c8ff00;}
.nav a.logo:hover		{background-color:#2a3133;}


/*	Hovering links for paging through posts
	============================================= */

.page_link			{display:inline-block; width:50%; vertical-align:top; margin-bottom:160px; position:relative;}
.page_link:nth-of-type(2)	{text-align:right;}
.page_link a		{display:inline; background-color:rgba(0,0,0,0.2); font-size:32px; color:#fff; padding:5px 20px; text-align:center; border-radius:4px; border:1px solid #545b73;}
.page_link a:hover	{background-color:rgba(53,57,69,0.6);}

.press_arrow 		{position:absolute; left:0; width:55px; text-align:center; color:#fff; line-height:15px; font-size:14px; margin-top:20px; text-shadow:0px 0px 5px #000;}
.page_link:nth-of-type(2) div	{left:initial; right:0;}


/*	Footer properties
	============================================= */

.foot		{padding:60px; background-color:#fff; border-top:1px solid #ddd; display:table-footer-group}
.foot_wrap	{width:960px; margin:0 auto; padding:60px 0;}
.foot_wrap p{padding:10px 0 5px 0;}
.text		{font-weight:300;}
.text span	{font-weight:400;}
.text.wetus	{text-align:right;}
.text.wetus *	{vertical-align:baseline;}
.text.wetus img	{width:initial;}

a.hi			{display:block; position:relative;}
a.hi span		{position:absolute; top:-30px; right:0; padding:5px 10px; color:#8900FF; display:none;}
a.hi:hover span	{display:block;}

a.back_top		{display:block; text-align:center; text-decoration:none; color:#666; padding:20px 0; font-size:14px;}
a.back_top:hover{background-color:rgba(0,0,0,0.2);}


/*	Post properties
	============================================= */

a.print, span.print_info, span.print_title, span.print_date, span.print_post	{display:block;}

.print		{background:url('{_dirname}/printscreen.pixel.png');}
.print_image	{width:960px; margin:0 auto; position:relative;}
.print_image img{width:100%; border:1px solid #42494B; border-bottom:0;}

.print_info	{padding:0 15px; margin-top:10px; font-weight:300;}
.print_title{font-weight:300;}
.print_post	{color:#202125; max-height:80px; height:80px; overflow:hidden;}
.print_date	{color:#2a3133;}
.print_date span	{font-weight:400;}

.print_post, .print_date	{display:inline-block; vertical-align:top;}
.print_post	{width:75%;}
.print_date	{width:20%; margin-left:2.5%; padding-left:2.5%; border-left:1px solid #ccc;}

.col img	{width:100%;}


/*	List of screenshots on the landing page
	============================================= */

.main			{position:relative;}
.main .wrap.page{bottom:50%;}
.main .page_link{margin:0;}

.print_wrap		{position:relative;}
.print_overlay	{position:absolute; width:100%; height:100%; padding:40px; background-color:rgba(255,255,255,0.8); top:0; left:0;}
.print_overlay	{display:none;}


/*	Screenshot on its own page
	============================================= */

.big .print_info{width:960px; margin:0 auto; padding:25px 20px; margin-bottom:40px;}
.big .print_post{height:initial; max-height:initial; overflow:initial; font-size:24px;}
.big .print_gradient	{padding-top:15px;}
.big .print_gradient div{padding-bottom:5px;}

a.full_image		{position:absolute; display:block; color:#fff; font-size:36px; padding:10px 20px; right:0; top:0; background-color:rgba(255,255,255,0.1);}
a.full_image:hover	{background-color:rgba(255,255,255,0.4);}


@-moz-document url-prefix() {
    body,html	{font-size:20px; line-height:1;}
    .nav a		{font-size:25px;}
    .big .print_post	{line-height:1.2;}
    .print_date	{font-size:18px;}
}


/*	PHONES & STUFF
	============================================= */

@media only screen and (-webkit-min-device-pixel-ratio: 1.5),
only screen and (-o-min-device-pixel-ratio: 3/2),
only screen and (min--moz-device-pixel-ratio: 1.5),
only screen and (min-device-pixel-ratio: 1.5) {

*			{width:100%; height:auto; min-height:1px; max-height:999999px;}

.wrap		{width:initial;}
.col, .col.col-2, .col.col-3	{display:block; width:90%; margin:0 auto; margin-bottom:40px;}

.main .wrap.page, .main .wrap.mob	{position:initial; display:block;}
.blog_list_wrap	{margin:0 auto;}

.nav		{padding:40px;}
.nav a		{font-size:64px;}
.nav a.logo	{width:200px;}
.nav a.logo span {border-bottom:8px solid #fff;}
.nav a.logo:hover span {border-bottom:8px solid #00d8ff;}

.print_info {padding:10px 40px;}
.big .print_info {padding:40px;}

.print_title{font-size:20px;}
.big .print_post	{height:initial; max-height:initial; overflow:initial; font-size:42px; line-height:1.6em;}
.print_date {font-size:28px;}

.wrap.page		{width:100%; left:0;}
.page_link		{margin:0;}
.page_link a	{display:block; font-size:64px;}

a.back_top		{padding:40px 0; font-size:30px;}

.press_arrow	{display:none;}

a.full_image, .keys	{display:none;}

.foot_wrap		{width:100%; padding:60px 40px;}
.foot_wrap div	{font-size:24px;}


@-moz-document url-prefix() {
    body,html	{font-size:20px;}
    .page_link a{font-size:48px;}
    .print_title{font-size:42px;}
    .print_post	{font-size:48px;}
    .print_date	{font-size:38px;}
}


}

