.main .wrap.page{bottom:50%;}
.main .page_link{margin:0;}

.print_wrap		{position:relative;}
.print_overlay	{position:absolute; width:100%; height:100%; padding:40px; background-color:rgba(255,255,255,0.8); top:0; left:0;}
